package main

import (
    "bufio"
    "bytes"
    "encoding/json"
    "fmt"
    "regexp"
    "strings"
)

var Output func(interface{})
var re = regexp.MustCompile("(.*)\\.(.*)(-.*)?\\.csv")

// data chunks in the pg_backup file begin with this instruction
var pgbDataChunkPrefixRE = regexp.MustCompile("COPY public.(.*) \\((.*)\\) FROM stdin;")

var pgbDataChunkSuffix = "\\."

func main() {
	// no special setup needed
}

func readTable(database string, r *bufio.Reader) (string, string, error) {
	// skip lines until the prefix for data chunks is found
	line, incomplete, err := r.ReadLine()
	var params []string = []string{}
	for err == nil && !incomplete && len(params) != 3 {
		params = pgbDataChunkPrefixRE.FindStringSubmatch(string(line))
		line, incomplete, err = r.ReadLine()
	}

	if err != nil {
		fmt.Println(err)
		return "", "", err
	}
	fmt.Println("Found table data chunk: ", params)

	// build schema from column names relying on naming standards for OpenStack tables
	// IDEA: use earlier CREATE TABLE statements to build a more efficient schema
	table := params[1]
	columns := strings.Split(params[2], ", ")

	fields := make([]map[string]interface{}, len(columns))
	for i := range columns {
		if columns[i] == "id" {
			fields[i] = map[string]interface{}{"name": columns[i], "type": "fixed", "size": 127}
		} else if columns[i] == "deleted" {
			fields[i] = map[string]interface{}{"name": columns[i], "type": []string{"boolean", "null"}}
		} else {
			// treat all fields except id as nullable (union type in Avro)
			fields[i] = map[string]interface{}{"name": columns[i], "type": []string{"string", "null"}}
		}
	}
	schema := map[string]interface{}{
		"namespace": database,
		"type":      "record",
		"name":      table,
		"fields":    fields,
	}

	schemaStr, err := json.Marshal(schema)
	if err != nil {
		fmt.Println("error:", err)
		return "", "", err
	}

	// read data line by line until the terminating suffix shows up
	payload := ""
	line, incomplete, err = r.ReadLine()
	for err == nil && !incomplete &&  string(line) != pgbDataChunkSuffix {
		values := strings.Split(string(line), "\t")
		for i := 0; i < len(values); i++ {
		    if values[i] == "\\N" {
		    	values[i] = ""
		    } else {
		        values[i] = strings.Replace(values[i], "\t", "    ", -1)
		    }
		}
		payload += strings.Join(values, "\t")
		line, incomplete, err = r.ReadLine()
	}

	return string(schemaStr), payload, nil
}

func schemaFromCSV(database string, table string, csv []byte) string {
    r := bufio.NewReaderSize(bytes.NewReader(csv), 1024)
    line, _, _ := r.ReadLine()
    columns := strings.Split(string(line), ",")

    fields := make([]map[string]interface{}, len(columns))
    for i := range columns {
        if columns[i] == "id" {
            fields[i] = map[string]interface{}{"name": columns[i], "type": "fixed", "size": 127}
        } else {
            // treat all fields except id as nullable (union type in Avro)
            fields[i] = map[string]interface{}{"name": columns[i], "type": []string{"string", "null"}}
        }
    }
    schema := map[string]interface{}{
        "namespace": database,
        "type": "record",
        "name": table,
        "fields": fields,
    }

    b, err := json.Marshal(schema)
	if err != nil {
		fmt.Println("error:", err)
	    return ""
	}
	return string(b)
}

// here implement the function with the input port name, so data coming from this input will call this function
func Input(val interface{}){
	message := val.(map[string]interface{})

	fmt.Println("ccloud input arrived")

	// read contents
	body := message["Body"].([]byte)
	attr := message["Attributes"].(map[string]interface{})

	fmt.Println("ccloud: contents read")

	// enhance header
    filename_seg := re.FindStringSubmatch(attr["storage.filename"].(string))
	if len(filename_seg) < 3 {
	    fmt.Println("ccloud: ERROR filename cannot be parsed")
	    return
	}

	database := filename_seg[1]

	r := bufio.NewReaderSize(bytes.NewReader(body), 8192)
	schema, body, err := readTable("Cinder", r))
    for err == nil && contents != "" {
    	newAttr := make(map[string]interface{}, len(attr)+2)
	    for key := range attr {
		    newAttr[key] = attr[key]
	    }
        newAttr["vora.record.definition"] = map[string]interface{}{"recName": schema["name"]}
        newAttr["avro.schema"] = schema

        // build transformed message
	    newMessage := make(map[string]interface{}, 3)
	    newMessage["Attributes"] = newAttr
	    newMessage["Encoding"] = message["Encoding"]
	    newMessage["Body"] = body

        // output
        fmt.Println("ccloud output body: ", string(body))
	    Output(newMessage)

        // next next
        schema, contents, err := readTable("Cinder", r))
    }
}
